import React,{useState} from 'react';
import {View, Text, TextInput, Button, SafeAreaView} from 'react-native';
import {useValue} from './ValueContext';

const addSpend = () => {

  const {currentValue,setCurrentValue} = useValue();
  const [spend,setSpend] = useState("");
  const [spentOn,setSpentOn] = useState("");
  const [description,setDescriptionn] = useState("");

  return (
    <SafeAreaView>
      <h2>Amount spent</h2>

      <TextInput
        style={styles.input}
        onChangeText={newText => setSpend(newText)}
      />

      <h2>Money spent on:</h2>

      <TextInput
        style={styles.input}
        onChangeText={newText => setSpentOn(newText)}
      />

      <h2>Description:</h2>

      <TextInput
        style={styles.input}
        onChangeText={newText => setDescriptionn(newText)}
      />

      <br />
      <br />
      <br />
      <br />

      <Button 
        title="save profile info"
        onPress = {() => {
        setCurrentValue({spend,spentOn,description});
        }}
        />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});

export default addSpend;
