import React from "react";
import { SafeAreaView, StyleSheet, TextInput, Button } from "react-native";

const addSpend = () => {

  return (
    <SafeAreaView>
      <h2>Amount spent</h2>

      <TextInput
        style={styles.input}
      />

      <h2>Money spent on:</h2>

      <TextInput
        style={styles.input}
      />

      <h2>Description:</h2>

      <TextInput
        style={styles.input}
      />

      <Button
          title="Save"
          onPress={() => Alert.alert('Right button pressed')}
        />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});

export default addSpend;
