import React,{useState} from 'react';
import {View,Text,TextInput,Button,SafeAreaView} from 'react-native';
import {useValue} from './ValueContext';

const addSpend = () => {

  return (
    <SafeAreaView>
      <h2>Amount spent</h2>

      <TextInput
        style={styles.input}
      />

      <h2>Money spent on:</h2>

      <TextInput
        style={styles.input}
      />

      <h2>Description:</h2>

      <TextInput
        style={styles.input}
      />

      <br />
      <br />
      <br />
      <br />

      <Button
          title="Save"
        />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});

export default addSpend;
